mapper.select:
select * from( select rownum as rn, i.* from ( select * from img_board order by i_no desc) i
where i.i_title like '%' || #{sval} || '%'
where i.i_content like '%' || #{sval} || '%'
where i.u_id like '%' || #{sval} || '%'
) where rn between #{list_start} and #{list_end}

mapper.update:
update img_board set i_title = #{i_title}, i_content = #{i_content}, i_date = sysdate
,i_originalFileName = #{i_originalFileName}, i_uploadFileName = #{i_uploadFileName}, i_fileSize = #{i_fileSize}
where i_no = #{i_no}
